javascript:(function(){'use strict';if(!('https://pupilpath.skedula.com'===window.location.origin))alert('This Only Works On PupilPath');else if(null!==document.getElementById('progress-card')){document.getElementsByClassName('notification information')[0].insertAdjacentHTML('afterbegin',`<span id='averageContainer'><a id='averageParent' style='color: #585b66; position: static; float: right; padding-top: 10px; padding-right: 10px;'>Total Average: <img id='averageIcon' /> <span id='totalAverage'></span></a></span>`);const a=document.querySelectorAll('table#progress-card > tbody > tr > td > span'),b=[];a.forEach(a=>{b.push(parseFloat(a.innerText))});const c=b.reduce((c,a)=>c+a),d=b.length,e=Math.round(100*(c/d))/100;let f,g,h;90<=e?(f='/img/ico/star.png',g='#0087FF',h='Honors'):80<=e?(f='/img/ico/tick.png',g='#1FBA24',h='Passing'):65<=e?(f='/img/ico/error.png',g='#AA9901',h='Borderline'):64>=e&&(f='/img/ico/exclamation.png',g='#CF1920',h='Failing'),document.getElementById('totalAverage').style.color=g,document.getElementById('averageIcon').src=f,document.getElementById('totalAverage').dataset.gtype=h,document.getElementById('totalAverage').innerText=`${e}%`}})();
